class FizStudentInfo {
  final String
      imagePath; // Store the file path or URL to the image instead of a base64 string
  final String name;
  final String email;
  final double ranking; // Assume ranking is between 0 and 100
  final double volHours; // Volunteer hours
  final double appGPA; // GPA, assuming a scale of 0 to 4

  FizStudentInfo({
    required this.imagePath,
    required this.name,
    required this.email,
    required this.ranking,
    required this.volHours,
    required this.appGPA,
  })  : assert(ranking >= 0 && ranking <= 100,
            'Ranking must be between 0 and 100'),
        assert(volHours >= 0, 'Volunteer hours cannot be negative'),
        assert(appGPA >= 0 && appGPA <= 5, 'GPA must be between 0 and 4');
  //hello
  static FizStudentInfo getCurrentStudent = FizStudentInfo(
    imagePath:
        "data:image/jpeg;base64,",
    name: "Dulat Kamytbek",
    email: "dulat.kamytbek25@fizmat.kz",
    ranking: 5.01,
    volHours: 26.5,
    appGPA: 4.561,
  );
}
